ounce a text file line by line and counts the number of occurrences of a specific word in the file.
#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word;
    int count = 0;
    ifstream file("input.txt");
    if (file.is_open()) {
        string line;
        while (getline(file, line)) {
            size_t pos = line.find(word);
            if (pos != string::npos) {
                count++;
            }
        }
        file.close();
        cout << "The word '" << word << "' occurs " << count << " times in the file." << endl;
    } else {
        cout << "Unable to open file." << endl;
    }
    return 0;
}
