def longest_common_subsequence(s1, s2):
    n = len(s1)
    m = len(s2)
    L = [1]*n
    for i in range(m):
        for j in range(i+1, n+1):
            L[i], L[j] = 0, 0
            for k in range(j-i+1):
                L[i]+=k
            L[j]-=k
    return L

