#include 
using namespace std;
int countWord(string filename, string word) {
 int count = 0;
 ifstream file(filename);
 if (file.is_open()) {
 while (file.good()) {
 string line;
 getline(file, line);
 if (line.find(word) != string::npos) {
 count++;
 }
 }
 file.close();
 }
 return count;
}
int main() {
 string filename = "text.txt";
 string word = "word";
 cout << "The word '" << word << "' appears " << countWord(filename, word) << " times in the text file." << endl;
 return 0;
}
