def longest_common_subsequence(x: str, y: str, m: int, n: int) -> str:
    lcp = [[0] * (n+1) for _ in range(m+1)]
    for i in range(m+1):
        for j in range(n+1):
            if i == 0 or j == 0:
                lcp[i][j] = 0
